import React, { Component } from 'react'
import Uppy from '@uppy/core';
import InputFile from '@uppy/file-input';
import Tus from '@uppy/tus';
import ProgressBar from '@uppy/progress-bar';

export default class FileInput extends Component {
  componentDidMount() {
    const uppy = Uppy({
      debug: true,
      autoProceed: true,
    });

    uppy.use(InputFile, {
      target: '#fileInput',
      pretty: false
    });

    uppy.use(Tus, { endpoint: 'https://master.tus.io/files/' })
      .use(ProgressBar, { target: '#progress', hideAfterFinish: false });

    $('#upload').click(function (e: any) {
      uppy.upload();
    })
    uppy.on('complete', ({ successful, failed }) => {
      if (successful)
        console.log('successful files:', successful[0].id)
      if (failed)
        console.log('failed files:', failed)
      uppy.removeFile(successful[0].id);
    })
  }

  render() {
    return (
      <div>
        <div id="fileInput" ></div>
        <div id="progress"></div>
        <button id="upload">Upload</button>
      </div>
    )
  }
}
